import{s as F,e as G,n as K}from"../chunks/scheduler.86e51451.js";import{S as N,i as U,r as q,u as H,v as I,d as L,t as O,w as z,g as o,s as b,h as y,B as f,c,k as B,a as u,f as l}from"../chunks/index.865ad39f.js";import{T as W}from"../chunks/TextArticle.05a845ac.js";import{B as E}from"../chunks/apa-itu-bullying-illust.43ae086d.js";import{B as J}from"../chunks/BottomNavigator.45599e8b.js";function Q(w){let n,s="Apa itu Bullying?",e,i,h,_,k,A="Bullying, juga dikenal sebagai perundungan atau pembulian dalam bahasa Indonesia, adalah suatu bentuk perilaku negatif yang menciptakan rasa takut, cemas, atau terluka pada korban.",v,m,M="<li><p>Menurut Olweus (2005), bullying diartikan sebagai tindakan atau perilaku agresif yang disengaja, yang dilakukan oleh sekelompok orang atau individu secara berulang kali terhadap korban yang tidak dapat mempertahankan dirinya dengan mudah, atau sebagai penyalahgunaan kekuasaan/kekuatan sistematis<sup>1</sup>.</p></li> <li><p>Menurut Wicaksana (2008), bullying adalah kekerasan fisik dan psikologis jangka panjang yang dilakukan seseorang atau kelompok, terhadap seseorang yang tidak mampu mempertahankan dirinya dalam situasi di mana ada hasrat untuk melukai atau menakuti orang itu atau membuat dia tertekan.</p></li> <li><p>Menurut American Psychatric Association (APA) bahwa bullying adalah perilaku agresif yang dikarakteristikkan dengan tiga kondisi yaitu: (a) perlikaku negative yang bertujuan untuk merusak atau membahayakan (b) perilaku yang diulang selama jangka waktu tertentu (c) adanya ketidakseimbangan kekuatan atau kekuasaan dari pihak-pihak yang terlibat. Beberapa kondisi tersebut lebih mengacu pada yang dapat menjadikan korban trauma, cemas dan sikap-sikap yang membuat tidak nyaman.</p></li>",x,r,S="Bullying dapat terjadi karena berbagai situasi dan dalam berbagai bentuk, baik secara langsung maupun tidak langsung. Salah satu penyebab utama bullying adalah perbedaan penampilan fisik. Ketika seorang anak memiliki penampilan fisik yang dianggap berbeda dari anak-anak lain, para bully bisa menjadikan hal tersebut sebagai bahan untuk mengintimidasi anak tersebut. Selain itu, keinginan untuk mendominasi juga bisa menjadi pemicu bullying. Pelaku bullying mungkin merasa dapat memperoleh dominasi terhadap orang lain dan memaksakan kehendaknya untuk mencapai kepuasan pribadi. Dalam ilmu psikologi, fenomena ini dikenal sebagai abuse of power, yaitu kecenderungan seseorang untuk mencari dominasi atau kekuasaan atas orang lain. Faktor lain yang bisa memicu bullying adalah pengaruh pergaulan yang tidak baik dan kurangnya empati.",j,g,T="Dampak bullying dapat sangat merugikan dan berlangsung dalam jangka pendek maupun jangka panjang. Dalam jangka pendek, korban bullying dapat mengalami ketakutan dan kecemasan. Sebagai contoh, anak yang menjadi korban bullying di sekolah mungkin akan merasa takut pergi ke sekolah, takut menggunakan kamar mandi, naik kendaraan umum, atau berjalan sendiri di lorong sekolah. Dalam jangka panjang, dampak bullying bisa lebih serius dan meliputi depresi, kecemasan, peningkatan risiko penggunaan narkoba, penurunan prestasi akademik, atau bahkan memicu anak untuk menjadi pelaku bullying di masa depan.",$,p,D="Mengatasi bullying adalah tugas penting bagi individu, komunitas, dan institusi untuk menciptakan lingkungan yang aman dan mendukung. Penting untuk mengajarkan anak-anak dan remaja tentang bullying, termasuk bagaimana mengidentifikasi perilaku bullying, bagaimana melaporkannya, dan bagaimana mendukung teman dan rekan yang mungkin menjadi korban bullying. Penanganan dan pencegahan bullying memerlukan pendekatan komprehensif yang melibatkan pendidikan, intervensi dini, dan dukungan sosial untuk membantu mencegah bullying dan mendukung korban dan keluarganya dalam mengatasi trauma yang mungkin diakibatkannya.",C,d,P;return d=new J({props:{nextPageUrl:"./apa-penyebab-bullying",nextPageTitle:"Apa Penyebab Bullying"}}),{c(){n=o("h1"),n.textContent=s,e=b(),i=o("img"),_=b(),k=o("p"),k.textContent=A,v=b(),m=o("ol"),m.innerHTML=M,x=b(),r=o("p"),r.textContent=S,j=b(),g=o("p"),g.textContent=T,$=b(),p=o("p"),p.textContent=D,C=b(),q(d.$$.fragment),this.h()},l(a){n=y(a,"H1",{["data-svelte-h"]:!0}),f(n)!=="svelte-17l2hup"&&(n.textContent=s),e=c(a),i=y(a,"IMG",{src:!0,id:!0,alt:!0,class:!0}),_=c(a),k=y(a,"P",{["data-svelte-h"]:!0}),f(k)!=="svelte-18w4xvb"&&(k.textContent=A),v=c(a),m=y(a,"OL",{["data-svelte-h"]:!0}),f(m)!=="svelte-mtqpc9"&&(m.innerHTML=M),x=c(a),r=y(a,"P",{["data-svelte-h"]:!0}),f(r)!=="svelte-1780p62"&&(r.textContent=S),j=c(a),g=y(a,"P",{["data-svelte-h"]:!0}),f(g)!=="svelte-1rktxbf"&&(g.textContent=T),$=c(a),p=y(a,"P",{["data-svelte-h"]:!0}),f(p)!=="svelte-1pz1pc6"&&(p.textContent=D),C=c(a),H(d.$$.fragment,a),this.h()},h(){G(i.src,h=E)||B(i,"src",h),B(i,"id","bullying-illust"),B(i,"alt",""),B(i,"class","svelte-o3nhwc")},m(a,t){u(a,n,t),u(a,e,t),u(a,i,t),u(a,_,t),u(a,k,t),u(a,v,t),u(a,m,t),u(a,x,t),u(a,r,t),u(a,j,t),u(a,g,t),u(a,$,t),u(a,p,t),u(a,C,t),I(d,a,t),P=!0},p:K,i(a){P||(L(d.$$.fragment,a),P=!0)},o(a){O(d.$$.fragment,a),P=!1},d(a){a&&(l(n),l(e),l(i),l(_),l(k),l(v),l(m),l(x),l(r),l(j),l(g),l($),l(p),l(C)),z(d,a)}}}function R(w){let n,s;return n=new W({props:{$$slots:{default:[Q]},$$scope:{ctx:w}}}),{c(){q(n.$$.fragment)},l(e){H(n.$$.fragment,e)},m(e,i){I(n,e,i),s=!0},p(e,[i]){const h={};i&1&&(h.$$scope={dirty:i,ctx:e}),n.$set(h)},i(e){s||(L(n.$$.fragment,e),s=!0)},o(e){O(n.$$.fragment,e),s=!1},d(e){z(n,e)}}}class na extends N{constructor(n){super(),U(this,n,null,R,F,{})}}export{na as component};
