import{s as N,n as R}from"../chunks/scheduler.86e51451.js";import{S as U,i as q,r as B,u as A,v as M,d as D,t as T,w as K,g as k,s as p,h as b,B as h,c as f,a as l,f as s}from"../chunks/index.865ad39f.js";import{B as H}from"../chunks/BottomNavigator.45599e8b.js";import{T as L}from"../chunks/TextArticle.05a845ac.js";function z(P){let e,u="Statistika Bullying di Indonesia",t,i,c="Bullying atau tindakan mengintimidasi dan menyakiti secara fisik maupun psikologis telah menjadi isu yang serius di Indonesia. Berbagai survei dan penelitian menunjukkan angka yang mencemaskan mengenai prevalensi bullying di kalangan pelajar di Indonesia.",y,r,j="Berdasarkan survei yang dilakukan oleh Programme for International Students Assessment (PISA) pada tahun 2018, Indonesia menempati posisi kelima tertinggi di dunia dalam persentase siswa yang mengalami bullying. Dari survei tersebut, terungkap bahwa 41,1% siswa di Indonesia melaporkan bahwa mereka pernah menjadi korban bullying.",v,m,w="Menteri Pendidikan, Kebudayaan, Riset, dan Teknologi (Mendikbud Ristek) juga mengungkapkan fakta yang sama melalui hasil Asesmen Nasional (AN). Menurut laporan tersebut, pada tahun 2021 dan 2022, sekitar 24,4% siswa di Indonesia mengalami berbagai bentuk bullying. Untuk menangani hal ini, pemerintah telah mengimplementasikan Program Roots Anti-Bullying sejak tahun 2021. Program ini dirancang khusus untuk mencegah aksi kekerasan di lembaga pendidikan. Dengan harapan pada tahun 2023, program ini dapat diterapkan lebih luas dan efektif.",x,g,S="Selain itu, gerakan anti-bullying bernama Sudah Dong juga telah melakukan penelitian dan mendapatkan data yang menunjukkan dampak serius dari bullying terhadap siswa di Indonesia. Statistik dari gerakan ini mengungkapkan bahwa 10% siswa memilih untuk berhenti sekolah akibat bullying. Lebih lanjut, 71% siswa menganggap bullying sebagai masalah besar di sekolah mereka. Paling mencemaskan, 90% siswa mulai dari Kelas 4 Sekolah Dasar hingga Kelas 2 SMP telah mengaku pernah menjadi korban bullying di sekolah.",$,o,I="Statistika di atas menunjukkan urgensi untuk mengatasi masalah bullying di Indonesia. Upaya dari berbagai pihak, mulai dari pemerintah, lembaga pendidikan, hingga masyarakat, sangat dibutuhkan untuk menciptakan lingkungan pendidikan yang kondusif dan bebas dari ancaman bullying.",_,d,C;return d=new H({props:{prevPageUrl:"./cara-mengendalikan-pembullyian",prevPageTitle:"Cara Mengendalikan Pembullyian"}}),{c(){e=k("h1"),e.textContent=u,t=p(),i=k("p"),i.textContent=c,y=p(),r=k("p"),r.textContent=j,v=p(),m=k("p"),m.textContent=w,x=p(),g=k("p"),g.textContent=S,$=p(),o=k("p"),o.textContent=I,_=p(),B(d.$$.fragment)},l(a){e=b(a,"H1",{["data-svelte-h"]:!0}),h(e)!=="svelte-1djh3h0"&&(e.textContent=u),t=f(a),i=b(a,"P",{["data-svelte-h"]:!0}),h(i)!=="svelte-1e09537"&&(i.textContent=c),y=f(a),r=b(a,"P",{["data-svelte-h"]:!0}),h(r)!=="svelte-1pdjhi8"&&(r.textContent=j),v=f(a),m=b(a,"P",{["data-svelte-h"]:!0}),h(m)!=="svelte-1kcpfjn"&&(m.textContent=w),x=f(a),g=b(a,"P",{["data-svelte-h"]:!0}),h(g)!=="svelte-reg0b3"&&(g.textContent=S),$=f(a),o=b(a,"P",{["data-svelte-h"]:!0}),h(o)!=="svelte-dvq2t"&&(o.textContent=I),_=f(a),A(d.$$.fragment,a)},m(a,n){l(a,e,n),l(a,t,n),l(a,i,n),l(a,y,n),l(a,r,n),l(a,v,n),l(a,m,n),l(a,x,n),l(a,g,n),l(a,$,n),l(a,o,n),l(a,_,n),M(d,a,n),C=!0},p:R,i(a){C||(D(d.$$.fragment,a),C=!0)},o(a){T(d.$$.fragment,a),C=!1},d(a){a&&(s(e),s(t),s(i),s(y),s(r),s(v),s(m),s(x),s(g),s($),s(o),s(_)),K(d,a)}}}function E(P){let e,u;return e=new L({props:{$$slots:{default:[z]},$$scope:{ctx:P}}}),{c(){B(e.$$.fragment)},l(t){A(e.$$.fragment,t)},m(t,i){M(e,t,i),u=!0},p(t,[i]){const c={};i&1&&(c.$$scope={dirty:i,ctx:t}),e.$set(c)},i(t){u||(D(e.$$.fragment,t),u=!0)},o(t){T(e.$$.fragment,t),u=!1},d(t){K(e,t)}}}class Q extends U{constructor(e){super(),q(this,e,null,E,N,{})}}export{Q as component};
